import styled from 'styled-components';

export const ButtonContainer = styled.button`
    padding: 10px;
    border: 1px solid #CDCDCD;
    background-color: #00AAF0;
    color: #FFF;
    font-size:24px; 
    font-weight: 700;
    flex:1;
    &:hover
 `
